package de.fleigm.transitrouter.feeds.evaluation;

import lombok.Data;
import lombok.experimental.Accessors;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;
import java.util.stream.Collectors;

/**
 * Represents the report file generated by the evaluation tool {@link Shapevl}.
 */
public class Report {
  private final List<Entry> entries;

  public Report(List<Entry> entries) {
    this.entries = entries;
  }

  /**
   * Load from full report tsv file.
   * @param path report file path.
   * @return Report
   */
  public static Report read(Path path) {
    try {
      List<Entry> entries1 = Files.lines(path)
          .map(line -> line.split("\t"))
          .map(values ->
              new Entry(
                  values[0],
                  Double.parseDouble(values[1]),
                  Double.parseDouble(values[2]),
                  Double.parseDouble(values[3])))
          .collect(Collectors.toList());

      return new Report(entries1);
    } catch (IOException e) {
      throw new RuntimeException(e);
    }
  }

  /**
   * @return entries.
   */
  public List<Entry> entries() {
    return entries;
  }

  @Data
  @Accessors(fluent = true)
  public static class Entry {
    public final String tripId;
    public final double an;
    public final double al;
    public final double avgFd;
    public int type;
  }
}
